class FolderConf {
  constructor(index, item){
    this.index = index
    this.nth = item[1]
    this.index_nth = 1
    this.maxSearchesAvailable = item[2]
    this.index_maxSearchesAvailable = 2
    this.maxThreads = item[3]
    this.index_maxThreads = 3
    this.maxItems = item[4]
    this.index_maxItems = 4
  }
}